import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { logout } from "../slices/userSlice";

export default function Layout({ children }) {
  const user = useSelector((state) => state.user.user);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleLogout = () => {
    dispatch(logout());
    navigate("/login");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex flex-col">
      <header className="bg-white shadow-md py-4 px-8 flex items-center justify-between">
        <div className="flex items-center gap-2">
          <span className="text-2xl font-bold text-indigo-700 tracking-tight">LUMEN Quest 2.0</span>
        </div>
        <nav className="flex gap-6 text-indigo-600 font-medium items-center">
          {!user && <Link to="/login" className="hover:text-indigo-900 transition">Login</Link>}
          {user?.role === "user" && (
            <>
              <Link to="/user" className="hover:text-indigo-900 transition">Dashboard</Link>
              <Link to="/user/plans" className="hover:text-indigo-900 transition">Plans</Link>
              <Link to="/user/subscriptions" className="hover:text-indigo-900 transition">Subscriptions</Link>
              <Link to="/user/recommendations" className="hover:text-indigo-900 transition">Recommendations</Link>
              <button onClick={handleLogout} className="ml-4 px-3 py-1 bg-indigo-100 rounded hover:bg-indigo-200 text-indigo-700 font-semibold transition">Logout</button>
            </>
          )}
          {user?.role === "admin" && (
            <>
              <Link to="/admin" className="hover:text-indigo-900 transition">Admin Dashboard</Link>
              <Link to="/admin/plans" className="hover:text-indigo-900 transition">Plans</Link>
              <Link to="/admin/discounts" className="hover:text-indigo-900 transition">Discounts</Link>
              <Link to="/admin/analytics" className="hover:text-indigo-900 transition">Analytics</Link>
              <button onClick={handleLogout} className="ml-4 px-3 py-1 bg-indigo-100 rounded hover:bg-indigo-200 text-indigo-700 font-semibold transition">Logout</button>
            </>
          )}
        </nav>
      </header>
      <main className="flex-1 w-full max-w-5xl mx-auto py-8 px-4">
        {children}
      </main>
      <footer className="bg-white text-center py-4 text-indigo-500 text-sm shadow-inner">
        &copy; {new Date().getFullYear()} LUMEN Quest 2.0 â€“ Subscription Management System
      </footer>
    </div>
  );
}
