function LoginPage() {
  const [role, setRole] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showSignup, setShowSignup] = useState(false);
  const [signupForm, setSignupForm] = useState({ name: '', phone: '', email: '', password: '', role: 'user' });
  const [signupError, setSignupError] = useState('');
  const [signupLoading, setSignupLoading] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { loading, error } = useSelector(state => state.user);

  const handleRoleSelect = (selectedRole) => {
    setRole(selectedRole);
    if (selectedRole === 'admin') setEmail('admin@example.com');
    else setEmail('user1@example.com');
    setPassword('');
    setShowSignup(false);
  };

  const handleSignupChange = (e) => {
    setSignupForm({ ...signupForm, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    dispatch(setLoading(true));
    try {
      const res = await axios.post('/api/auth/login', { email, password });
      dispatch(setUser(res.data));
      if (res.data.user.role === 'admin') navigate('/admin');
      else navigate('/user');
    } catch (err) {
      dispatch(setError(err.response?.data?.message || 'Login failed'));
      setShowSignup(true);
    } finally {
      dispatch(setLoading(false));
    }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setSignupLoading(true);
    setSignupError('');
    try {
      await axios.post('/api/auth/register', { ...signupForm, status: 'active' });
      setShowSignup(false);
      setRole('');
      setEmail('');
      setPassword('');
      alert('Signup successful! Please login.');
    } catch (err) {
      setSignupError(err.response?.data?.message || 'Signup failed');
    }
    setSignupLoading(false);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-100 to-indigo-200">
      <div className="bg-white/90 p-10 rounded-2xl shadow-2xl w-full max-w-md border border-indigo-100">
        <div className="flex flex-col items-center mb-6">
          <img src="https://img.icons8.com/color/96/000000/internet--v2.png" alt="LUMEN Logo" className="mb-2" />
          <h2 className="text-3xl font-extrabold text-indigo-700 mb-1 tracking-tight">LUMEN Quest Login</h2>
          <p className="text-indigo-400 text-sm">Subscription Management System</p>
        </div>
        {!role && !showSignup && (
          <div className="flex flex-col gap-4 mb-6">
            <button onClick={() => handleRoleSelect('admin')} className="w-full bg-indigo-600 text-white py-2 rounded-lg font-semibold shadow hover:bg-indigo-700 transition">Login as Admin</button>
            <button onClick={() => handleRoleSelect('user')} className="w-full bg-blue-500 text-white py-2 rounded-lg font-semibold shadow hover:bg-blue-600 transition">Login as User</button>
          </div>
        )}
        {role && !showSignup && (
          <form onSubmit={handleSubmit}>
            {error && <div className="mb-4 text-red-500 text-center font-medium bg-red-50 border border-red-200 rounded p-2">{error}</div>}
            <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} className="w-full mb-4 p-3 border border-indigo-200 rounded-lg focus:ring-2 focus:ring-indigo-300 outline-none transition" required />
            <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} className="w-full mb-6 p-3 border border-indigo-200 rounded-lg focus:ring-2 focus:ring-indigo-300 outline-none transition" required />
            <button type="submit" className="w-full bg-gradient-to-r from-indigo-500 to-blue-500 text-white py-3 rounded-lg font-semibold shadow hover:from-indigo-600 hover:to-blue-600 transition" disabled={loading}>
              {loading ? 'Logging in...' : 'Login'}
            </button>
            <button type="button" onClick={() => setRole('')} className="w-full mt-2 text-indigo-500 underline">Back</button>
          </form>
        )}
        {showSignup && (
          <form onSubmit={handleSignup} className="mt-8">
            <h3 className="text-xl font-bold text-indigo-700 mb-4">Sign Up</h3>
            {signupError && <div className="text-red-600 mb-2">{signupError}</div>}
            <input name="name" value={signupForm.name} onChange={handleSignupChange} placeholder="Name" className="w-full mb-3 p-2 border rounded" required />
            <input name="phone" value={signupForm.phone} onChange={handleSignupChange} placeholder="Phone" className="w-full mb-3 p-2 border rounded" required />
            <input name="email" value={signupForm.email} onChange={handleSignupChange} placeholder="Email" type="email" className="w-full mb-3 p-2 border rounded" required />
            <input name="password" value={signupForm.password} onChange={handleSignupChange} placeholder="Password" type="password" className="w-full mb-3 p-2 border rounded" required />
            <div className="mb-3 flex items-center gap-4">
              <label className="mr-2">Role:</label>
              <label className="flex items-center gap-1">
                <input type="radio" name="role" value="user" checked={signupForm.role === 'user'} onChange={handleSignupChange} /> User
              </label>
              <label className="flex items-center gap-1">
                <input type="radio" name="role" value="admin" checked={signupForm.role === 'admin'} onChange={handleSignupChange} /> Admin
              </label>
            </div>
            <button type="submit" className="w-full bg-indigo-600 text-white py-2 rounded hover:bg-indigo-700 transition">
              {signupLoading ? 'Signing up...' : 'Sign Up'}
            </button>
            <button type="button" onClick={() => { setShowSignup(false); setRole(''); }} className="w-full mt-2 text-indigo-500 underline">Back to Login</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default LoginPage;
