import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { setUser, setLoading, setError } from '../slices/userSlice';

function LoginPage() {
  const [role, setRole] = React.useState('user');
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');
  const [showSignup, setShowSignup] = React.useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { loading, error } = useSelector(state => state.user);

  const handleSubmit = async (e) => {
    e.preventDefault();
    dispatch(setLoading(true));
    try {
      const res = await axios.post('/api/auth/login', { email, password, role });
      dispatch(setUser(res.data));
      if (res.data.user.role === 'admin') navigate('/admin');
      else navigate('/user');
    } catch (err) {
      dispatch(setError(err.response?.data?.message || 'Login failed'));
      setShowSignup(true);
    } finally {
      dispatch(setLoading(false));
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-200 via-blue-100 to-white px-4">
      <div className="w-full max-w-md bg-white/95 rounded-3xl shadow-2xl border border-indigo-100 p-10 flex flex-col items-center">
        <div className="flex flex-col items-center mb-8">
          <div className="bg-gradient-to-tr from-indigo-500 to-blue-400 p-4 rounded-full shadow-lg mb-3">
            <img src="https://img.icons8.com/color/96/000000/internet--v2.png" alt="LUMEN Logo" className="w-16 h-16" />
          </div>
          <h1 className="text-4xl font-extrabold text-indigo-700 mb-1 tracking-tight text-center">LUMEN Quest</h1>
          <p className="text-indigo-400 text-base font-medium text-center">Subscription Management System</p>
        </div>
        {!showSignup ? (
          <form onSubmit={handleSubmit} className="w-full flex flex-col gap-6">
            {error && <div className="mb-2 text-red-500 text-center font-medium bg-red-50 border border-red-200 rounded p-2 animate-pulse">{error}</div>}
            <div className="flex flex-col gap-2">
              <label htmlFor="email" className="text-indigo-700 font-semibold">Email</label>
              <input
                id="email"
                type="email"
                value={email}
                onChange={e => setEmail(e.target.value)}
                className="w-full p-3 border border-indigo-200 rounded-lg focus:ring-2 focus:ring-indigo-400 outline-none transition placeholder-indigo-300"
                placeholder="Enter your email"
                required
              />
            </div>
            <div className="flex flex-col gap-2">
              <label htmlFor="password" className="text-indigo-700 font-semibold">Password</label>
              <input
                id="password"
                type="password"
                value={password}
                onChange={e => setPassword(e.target.value)}
                className="w-full p-3 border border-indigo-200 rounded-lg focus:ring-2 focus:ring-indigo-400 outline-none transition placeholder-indigo-300"
                placeholder="Enter your password"
                required
              />
            </div>
            <div className="flex flex-col gap-2">
              <span className="text-indigo-700 font-semibold">Login as</span>
              <div className="flex gap-6 justify-center">
                <label className="flex items-center gap-2 cursor-pointer group">
                  <input
                    type="radio"
                    name="role"
                    value="admin"
                    checked={role === 'admin'}
                    onChange={() => setRole('admin')}
                    className="accent-indigo-600 w-5 h-5 transition-all duration-200 group-hover:scale-110 focus:ring-2 focus:ring-indigo-400"
                  />
                  <span className="text-indigo-600 font-medium group-hover:text-indigo-800 transition">Admin</span>
                </label>
                <label className="flex items-center gap-2 cursor-pointer group">
                  <input
                    type="radio"
                    name="role"
                    value="user"
                    checked={role === 'user'}
                    onChange={() => setRole('user')}
                    className="accent-blue-500 w-5 h-5 transition-all duration-200 group-hover:scale-110 focus:ring-2 focus:ring-blue-400"
                  />
                  <span className="text-blue-600 font-medium group-hover:text-blue-800 transition">User</span>
                </label>
              </div>
            </div>
            <button
              type="submit"
              className="w-full bg-gradient-to-r from-indigo-500 to-blue-500 text-white py-3 rounded-xl font-bold shadow-lg hover:from-indigo-600 hover:to-blue-600 focus:ring-2 focus:ring-indigo-400 transition-all text-lg tracking-wide"
              disabled={loading}
            >
              {loading ? 'Logging in...' : 'Login'}
            </button>
          </form>
        ) : (
          <div className="w-full flex flex-col items-center justify-center py-8">
            <h2 className="text-xl font-bold text-indigo-700 mb-4">Sign Up</h2>
            {/* TODO: Add signup form here */}
            <div className="text-indigo-500">Signup form goes here.</div>
            <button className="mt-4 text-indigo-600 underline" onClick={() => setShowSignup(false)}>Back to Login</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default LoginPage;
