import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setUser, setLoading, setError } from '../slices/userSlice';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { loading, error } = useSelector(state => state.user);

  const handleSubmit = async (e) => {
    e.preventDefault();
    dispatch(setLoading(true));
    try {
      const res = await axios.post('/api/auth/login', { email, password });
      dispatch(setUser(res.data));
      if (res.data.user.role === 'admin') navigate('/admin');
      else navigate('/user');
    } catch (err) {
      dispatch(setError(err.response?.data?.message || 'Login failed'));
    } finally {
      dispatch(setLoading(false));
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-100 to-indigo-200">
      <form onSubmit={handleSubmit} className="bg-white/90 p-10 rounded-2xl shadow-2xl w-full max-w-md border border-indigo-100">
        <div className="flex flex-col items-center mb-6">
          <img src="https://img.icons8.com/color/96/000000/internet--v2.png" alt="LUMEN Logo" className="mb-2" />
          <h2 className="text-3xl font-extrabold text-indigo-700 mb-1 tracking-tight">LUMEN Quest Login</h2>
          <p className="text-indigo-400 text-sm">Subscription Management System</p>
        </div>
        {error && <div className="mb-4 text-red-500 text-center font-medium bg-red-50 border border-red-200 rounded p-2">{error}</div>}
        <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} className="w-full mb-4 p-3 border border-indigo-200 rounded-lg focus:ring-2 focus:ring-indigo-300 outline-none transition" required />
        <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} className="w-full mb-6 p-3 border border-indigo-200 rounded-lg focus:ring-2 focus:ring-indigo-300 outline-none transition" required />
        <button type="submit" className="w-full bg-gradient-to-r from-indigo-500 to-blue-500 text-white py-3 rounded-lg font-semibold shadow hover:from-indigo-600 hover:to-blue-600 transition" disabled={loading}>
          {loading ? 'Logging in...' : 'Login'}
        </button>
      </form>
    </div>
  );
}

export default LoginPage;
